import React, { useState } from 'react';
import axios from 'axios';
import { Container, Button, Form, Card } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const Chatbot = () => {
  const [answers, setAnswers] = useState([]);
  const [response, setResponse] = useState('');

  const questions = [
    'How are you feeling today?',
    'How well did you sleep?',
    'How stressed are you right now?'
  ];

  const handleSubmit = async () => {
    try {
      const { data } = await axios.post('http://localhost:5000/api/chatbot/generate-response', { answers }, { withCredentials: true });
      setResponse(data.reply);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <Container className="d-flex justify-content-center align-items-center vh-100">
      <Card style={{ width: '30rem', padding: '20px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)' }}>
        <h2 className="text-center mb-4">Mental Health Chatbot</h2>
        {questions.map((q, index) => (
          <Form.Group key={index} className="mb-3">
            <Form.Label>{q}</Form.Label>
            <Form.Select onChange={(e) => {
              const newAnswers = [...answers];
              newAnswers[index] = e.target.value;
              setAnswers(newAnswers);
            }}>
              <option>Select an answer</option>
              <option value="Happy">Happy ðŸ˜Š</option>
              <option value="Stressed">Stressed ðŸ˜–</option>
              <option value="Tired">Tired ðŸ˜´</option>
              <option value="Sad">Sad ðŸ˜”</option>
            </Form.Select>
          </Form.Group>
        ))}
        <Button variant="primary" onClick={handleSubmit} className="w-100 mt-3">Submit</Button>
        {response && (
          <Card className="mt-4 p-3 text-center" style={{ backgroundColor: '#f8f9fa' }}>
            <strong>AI Response:</strong> {response}
          </Card>
        )}
      </Card>
    </Container>
  );
};

export default Chatbot;
